const stats = $('Fetch Stats').first().json[0] || {};
const topProducts = $('Top Products').all().map(item => item.json);

return [{
  json: {
    success: true,
    stats: {
      orders: {
        total: parseInt(stats.total_orders) || 0,
        pending: parseInt(stats.pending_orders) || 0,
        confirmed: parseInt(stats.confirmed_orders) || 0,
        processing: parseInt(stats.processing_orders) || 0,
        shipped: parseInt(stats.shipped_orders) || 0,
        delivered: parseInt(stats.delivered_orders) || 0,
        cancelled: parseInt(stats.cancelled_orders) || 0,
        refunded: parseInt(stats.refunded_orders) || 0
      },
      revenue: {
        total: parseFloat(stats.total_revenue) || 0,
        completed: parseFloat(stats.completed_revenue) || 0
      },
      recent: {
        orders_24h: parseInt(stats.orders_24h) || 0,
        orders_7d: parseInt(stats.orders_7d) || 0,
        revenue_24h: parseFloat(stats.revenue_24h) || 0,
        revenue_7d: parseFloat(stats.revenue_7d) || 0
      },
      top_products: topProducts
    },
    generated_at: new Date().toISOString()
  }
}];
